// SingleBlogPage.jsx
import React from 'react';
import { useParams } from 'react-router-dom';
import { Link } from 'react-router-dom';

const blogs = [
  {
    id: 1,
    title: 'Blog Title 1',
    description: 'This is a short description of blog 1.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog 1...'
  },
  {
    id: 2,
    title: 'Blog Title 2',
    description: 'This is a short description of blog 2.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog 2...'
  },
  {
    id: 3,
    title: 'Blog Title 3',
    description: 'This is a short description of blog 3.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog 3...'
  },
  {
    id: 4,
    title: 'Blog Title 4',
    description: 'This is a short description of blog 4.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog 4...'
  },
  {
    id: 5,
    title: 'Blog Title 5',
    description: 'This is a short description of blog 5.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog 5...'
  }
];

function SingleBlogPage() {
  const { id } = useParams();
  const blog = blogs.find(blog => blog.id === parseInt(id));

  if (!blog) {
    return <h2>Blog not found!</h2>;
  }

  return (
    <div>
      <h1>{blog.title}</h1>
      <img src={blog.imageUrl} alt={blog.title} />
      <p>{blog.content}</p>
      <Link to="/">Back to Blog List</Link>
    </div>
  );
}

export default SingleBlogPage;
