// BlogComponent.jsx
import React from 'react';
import { Link } from 'react-router-dom';

const blogs = [
  {
    id: 1,
    title: 'Blog Title 1',
    description: 'This is a short description of blog 1.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog ...'
  },
  {
    id: 2,
    title: 'Blog Title 2',
    description: 'This is a short description of blog 2.',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog ...'
  },
  {
    id: 3,
    title: 'Blog Title 3',
    description: 'This is a short description of blog .',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog ...'
  },
  {
    id: 4,
    title: 'Blog Title 4',
    description: 'This is a short description of blog .',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog ...'
  },
  {
    id: 5,
    title: 'Blog Title 5',
    description: 'This is a short description of blog .',
    imageUrl: 'https://via.placeholder.com/150',
    content: 'Full blog content for Blog ...'
  }
];

function BlogComponent() {
  return (
    <div className="blog-grid container-with">
      {blogs.map(blog => (
        <div className="blog-card" key={blog.id}>
          <img src={blog.imageUrl} alt={blog.title} />
          <h2>{blog.title}</h2>
          <p>{blog.description}</p>
          <Link to={`/blog/${blog.id}`}>Read More</Link>
        </div>
      ))}
    </div>
  );
}

export default BlogComponent;
